import React, { useState } from "react";
import { HomeIcon } from "@heroicons/react/24/outline";
import { ChatBubbleLeftIcon } from "@heroicons/react/24/outline";
import { UserIcon } from "@heroicons/react/24/outline";
import { BookmarkIcon } from "@heroicons/react/24/outline";
import { Cog6ToothIcon } from "@heroicons/react/24/outline";

import styles from "./NavBar.module.css";
import { click } from "@testing-library/user-event/dist/click";

const NavBar = () => {
  const [index, setIndex] = useState(0);
  icons = [
    <HomeIcon className={styles["icon-nav"]} />,
    <ChatBubbleLeftIcon className={styles["icon-nav"]} />,
    <UserIcon className={styles["icon-nav"]} />,
    <BookmarkIcon className={styles["icon-nav"]} />,
    <Cog6ToothIcon className={styles["icon-nav"]} />,
  ];

  const handleActive = (e) => {
    const clicked = e.target;
    if(!clicked) return;
    console.log(clicked.dataset)
    setIndex(clicked.dataset.index)
  }
  return (
    <div className={styles["nav-main"]}>
      <div className={styles["nav-logo"]}>
        <img src="" alt="logo" />
      </div>

      <nav className={styles["items-nav"]}>
        <ul onClick={handleActive}>
          {icons.map((icon, i) => {
            return (
              <li>
                <a
                  href=""
                  data-index={i}
                  className={
                    styles[
                      `items-nav ${i === index ? "items-nav__active" : ""}`
                    ]
                  }
                >
                  {icon}
                </a>
              </li>
            );
          })}
        </ul>
      </nav>
    </div>
  );
};

export default NavBar;
